


class Triangle
{
	void disp()
	{
		int i, j;
		for(i=0; i<=5;i++)
		{
			for(j=0;j<=i;j++)
				
			{
				
				System.out.print("*");
			}
			System.out.println();
		}
		
	}

}


public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Triangle obj = new Triangle();
obj.disp();
	}

}
